import React, { useEffect, useState } from 'react';
import { FiPower, FiTrash } from 'react-icons/fi'

import api from '../../services/api';
import './styles.css';

export default function Carrinho() {
    const [cart, setCart] = useState([]);

    const nameUser = localStorage.getItem('nameUser');
    const loginUser = localStorage.getItem('loginUser');

    useEffect(() => {
        api.get('cart',`${loginUser}`).then(response => {
            setCart(response.data)
        });
    }, [loginUser]);

    return (
        <div className="profile-container">
            <header>
                <span>Sua lista de compras, {nameUser}</span>
                <p className="button">Valor:100,00</p>
                <button type="button">
                    <FiPower size={18} color="#E02041" />
                </button>
            </header>

            <h1>Seus Produtos</h1>
            <ul>
                {cart.map(cart => (
                    <li key={cart.id}>
                        <strong>Descrição: </strong>
                        <p>{cart.descricao}</p>

                        <strong>Valor: </strong>
                        <p>{Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(cart.preco)}</p>

                        <button type="button">
                            <FiTrash size={20} color="#a8a8b3" />
                        </button>
                    </li>

                ))}
            </ul>
        </div>
    );
}
